import React, { useState, useEffect } from 'react'

function Info() {

    //таймер
    //дата и время
    // const [isSeconds, setIsSeconds] = useState(60);
    // const [isMinuts, setMinuts] = useState(0);
    // const [isHours, setHours] = useState(0);
    //setInterval(() => { console.log(1) }, 1000);

    const [isSeconds, setIsSeconds] = React.useState(10); // секунды
    const [isMinuts, setMinuts] = useState(0); // минуты
    //const [isHours, setHours] = useState(60); // часы 

    const id = React.useRef(null);
    const clear = () => {
        window.clearInterval(id.current);
    };
    React.useEffect(() => {
        id.current = window.setInterval(() => {
            setIsSeconds((time) => time - 1);
        }, 1000);
        return () => clear();
    }, []);

    React.useEffect(() => {
        if (isSeconds === 0 & isMinuts === 0) {
            clear();
        }
        if (isMinuts > 0) {
            setMinuts(isMinuts - 1);
        }
    }, [isSeconds, isMinuts]);



    return (
        <div className='info'>
            <p className='info_text'>Уважаемые участники, во время вашего хода вы можете изменить параметры торгов, указанные в таблице:</p>
            <div className='info_motion'>
                <p className='info_motion-text'>ХОД</p>
                <div className='info_timer'>{isSeconds}</div>
            </div>
        </div>
    );
}

export default Info;
