import { useState, useEffect } from "react";

function Timer1() {
    const [over, setOver] = useState(false);
    const [[hours, minutes, seconds], setTime] = useState([0, 2, 0]);

    const tick = () => {
        if (over) return;
        if (hours === 0 && minutes === 0 && seconds === 0) {
            setOver(true);
        } else if (minutes === 0 && seconds === 0) {
            setTime([hours - 1, 59, 59]);
        } else if (seconds === 0) {
            setTime([hours, minutes - 1, 59]);
        } else {
            setTime([hours, minutes, seconds - 1]);
        }
    };

    const reset = () => {
        setTime([parseInt(0), parseInt(2), parseInt(0)]);
        setOver(true);
    };

    useEffect(() => {
        const timerID = setInterval(() => tick(), 1000);
        return () => clearInterval(timerID);
    }, [over, seconds]);

    return (
        <h2 className='info_timer'>{hours}:{minutes}:
            {seconds}</h2>
    )
}

export default Timer1;
