import React, { useEffect, useState } from 'react';

function Timer() {
    let target_date = new Date().getTime() + (1000 * 3600 * 48); // установить дату обратного отсчета
    let days, hours, minutes, seconds; // переменные для единиц времени

    function pad(n) {
        return (n < 10 ? '0' : '') + n;
    }

    function getCountdown() {

        let current_date = new Date().getTime();
        let seconds_left = (target_date - current_date) / 1000;

        days = pad(parseInt(seconds_left / 86400));
        seconds_left = seconds_left % 86400;

        hours = pad(parseInt(seconds_left / 3600));
        seconds_left = seconds_left % 3600;

        minutes = pad(parseInt(seconds_left / 60));
        seconds = pad(parseInt(seconds_left % 60));
        return seconds;
    }




    getCountdown();

    setInterval(function () { getCountdown(); }, 1000);

    //const [isDate, setIsDate] = useState(new Date());
    //setInterval(() => { setIsDate(new Date()); console.log(new Date().getSeconds()) }, 1000); // обновить дату раз в секунду

    // useEffect(() => {
    //setInterval(function () { getCountdown(); }, 1000);
    //     console.log(1)
    //setInterval(function () { console.log(1) }, 1000);
    //}, [isDate]);

    return (
        <div className='info_timer'>{seconds}</div>)
}

export default Timer;
