import './style/app.css';
import React, { useState, useEffect } from 'react';

function App() {

  //дата и время
  let isDate = new Date();
  let dateNow = isDate.getDate() + '.' + isDate.getMonth() + '.' + isDate.getFullYear(); // получить дату;
  let timeNow = isDate.getHours() + ':' + isDate.getMinutes() + ':' + isDate.getSeconds(); // получить время

  function isDateNow() {
    isDate = new Date();
    dateNow = isDate.getDate() + '.' + isDate.getMonth() + '.' + isDate.getFullYear(); // получить дату;
    timeNow = isDate.getHours() + ':' + isDate.getMinutes() + ':' + isDate.getSeconds(); // получить время
    console.log('ctr')
  };
  setTimeout(isDateNow, 1000);

  return (
    <div className="app">
      <header className="header">
        <h2 className='header_title'>Ход торгов</h2>
        <p className='header_text'>Тестовые торги на аппарат ЛОТОС №2033564 ({dateNow + ' ' + timeNow})
        </p>
      </header>
    </div>
  );
}

export default App;
