import React, { useState, useEffect } from 'react'

function Info() {

    //таймер
    //дата и время

    const [paused, setPaused] = React.useState(false);
    const [over, setOver] = React.useState(false);
    const [[hours, minutes, se], setTime] = React.useState([1, 11, 11]);

    const tick = () => {
        if (paused || over) return;
        if (h === 0 && m === 0 && s === 0) {
            setOver(true);
        } else if (m === 0 && s === 0) {
            setTime([h - 1, 59, 59]);
        } else if (s == 0) {
            setTime([h, m - 1, 59]);
        } else {
            setTime([h, m, s - 1]);
        }
    };

    const reset = () => {
        setTime([parseInt(1), parseInt(11), parseInt(11)]);
        setPaused(false);
        setOver(false);
    };

    React.useEffect(() => {
        const timerID = setInterval(() => tick(), 1000);
        return () => clearInterval(timerID);
    });



    return (
        <div className='info'>
            <p className='info_text'>Уважаемые участники, во время вашего хода вы можете изменить параметры торгов, указанные в таблице:</p>
            <div className='info_motion'>
                <p className='info_motion-text'>ХОД</p>
                <div className='info_timer'>{h}:{m}:{s}</div>
            </div>
        </div>
    );
}

export default Info;
