import './style/app.css';
import React, { useState, useEffect } from 'react';

function App() {

  // дата и время
  const [isDate, setIsDate] = useState(new Date());
  useEffect(() => {
    setIsDate(new Date());
  }, [isDate]); // установить зависимость от 

  let dateNow = isDate.getDate() + '.' + isDate.getMonth() + '.' + isDate.getFullYear(); // получить дату
  let timeNow = isDate.getHours() + ':' + isDate.getMinutes() + ':' + isDate.getSeconds(); // получить время


  return (
    <div className="app">
      <header className="header">
        <h2>Ход торгов</h2>
        <p>Тестовые торги на аппарат ЛОТОС №2033564 ({dateNow + ' ' + timeNow})
        </p>
      </header>
    </div>
  );
}

export default App;
