import React, { useState, useEffect } from 'react'
import Timer from './../../utils/Timer/Timer'

function Info() {

    //таймер
    //дата и время

    // const [over, setOver] = useState(false);
    // const [[hours, minutes, seconds], setTime] = useState([0, 7, 60]);

    // const tick = () => {
    //     if (over) return;
    //     if (hours === 0 && minutes === 0 && seconds === 0) {
    //         setOver(true);
    //     } else if (minutes === 0 && seconds === 0) {
    //         setTime([hours - 1, 59, 59]);
    //     } else if (seconds === 0) {
    //         setTime([hours, minutes - 1, 59]);
    //     } else {
    //         setTime([hours, minutes, seconds - 1]);
    //     }
    //     console.log(seconds)
    // };

    // const reset = () => {
    //     setTime([parseInt(0), parseInt(7), parseInt(60)]);
    //     setOver(true);
    // };

    // useEffect(() => {
    //     const timerID = setInterval(() => tick(), 1000);
    //     return () => clearInterval(timerID); 
    // });

    let target_date = new Date().getTime() + (1000 * 3600 * 48); // установить дату обратного отсчета
    let days, hours, minutes, seconds; // переменные для единиц времени

    setInterval(function () { getCountdown(); }, 1000);

    function pad(n) {
        return (n < 10 ? '0' : '') + n;
    }

    function getCountdown() {

        let current_date = new Date().getTime();
        let seconds_left = (target_date - current_date) / 1000;

        days = pad(parseInt(seconds_left / 86400));
        seconds_left = seconds_left % 86400;

        hours = pad(parseInt(seconds_left / 3600));
        seconds_left = seconds_left % 3600;

        minutes = pad(parseInt(seconds_left / 60));
        seconds = pad(parseInt(seconds_left % 60));

        // строка обратного отсчета  + значение тега
        return days + " " + hours + " " + minutes + " " + seconds;
    }

    getCountdown();


    return (
        <div className='info'>
            <p className='info_text'>Уважаемые участники, во время вашего хода вы можете изменить параметры торгов, указанные в таблице:</p>
            <div className='info_motion'>
                <p className='info_motion-text'>ХОД</p>
                <div className='info_timer'>{hours, minutes, seconds}</div>
            </div>
        </div>
    );
}

export default Info;
